
public class Swap {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5, b = 6, c;
		System.out.println("Befor Swapping");
		System.out.println("Value of a : "+a);
		System.out.println("Value of b : "+b);
		c = a; a = b;
		b = c;		
		System.out.println("After Swapping");
		System.out.println("Value of a : "+a);
		System.out.println("Value of b : "+b);			
	}
}
